<?php ini_set('display_errors',1);ini_set('display_startup_errors',1);error_reporting(E_ALL);if(!session_id()){session_start();}require_once("../server/global.php");$conn=get_connection();function fail($err,$line=0){global $conn;echo "<i style='color:red'>".__FILE__." in line ".$line.". Error: ".$err."</i>";$conn->close();exit;}$thisUserId=isset($_SESSION['userId'])?$_SESSION['userId']:-1;if(isset($_GET['username'])){$uname=$conn->real_escape_string(trim(urldecode($_GET['username'])));}else{echo "No profile to show...";exit;}$res=$conn->query("SELECT user.Id as id,CONCAT(user.FirstName, ' ', user.LastName) as fullName,user.Email as email,user.Phone as phone,user.Location as location,user.Intro as intro,user.PictureReal as picture,GROUP_CONCAT(qn.Id) as questions,(COUNT(uf.FollowedBy) != 0) as isFollowing,(    SELECT COUNT(FollowedTo) FROM UserFollow WHERE FollowedTo=user.Id) AS followers,(    SELECT COUNT(FollowedBY) FROM UserFollow WHERE FollowedBy=user.Id) AS following,(    SELECT GROUP_CONCAT(ans.Id) FROM Answer ans WHERE ans.Author=user.Id) AS answers FROM User user LEFT JOINQuestion qnON user.Id = qn.Author LEFT JOINUserFollow ufON (uf.FollowedBy = $thisUserId) AND (uf.FollowedTo=user.Id) Where user.UserName = '$uname';")or fail($conn->error,__LINE__);$res=$res->fetch_all(MYSQLI_ASSOC)[0];$allAnswers=$res['answers'];$allQuestions=$res['questions'];$allAnswers=($allAnswers==null)?-1:$allAnswers;$allQuestions=($allQuestions==null)?-1:$allQuestions;$UserId=$res['id'];if($UserId==null){echo "<h1>We cannot get any user for this data.<br>Signin or share us with your friends to get registered on this id</h1>";$conn->close();exit;}$UserName=$res['fullName'];$UserEmail=$res['email'];$UserIntro=$res['intro'];$UserLocation=$res['location'];$UserPicture=$res['picture'];$UserPhone=$res['phone'];$isFollowing=($res['isFollowing']==1)?true:false;$FollowersCount=$res['followers'];$FollowingCount=$res['following'];$AnswerCount=($allAnswers==-1)?0:count(explode(',',$allAnswers));$QuestionCount=($allQuestions==-1)?0:count(explode(',',$allQuestions));$ClapsCount=0;if(strlen($allAnswers)>0){$res=$conn->query("SELECT
            (
                SELECT COUNT(User) FROM QuestionClaps WHERE Question IN ($allQuestions)
            ),
            (
                SELECT COUNT(user) FROM AnswerClaps WHERE Answer IN ($allAnswers)
            )
        ;")or fail($conn->error,__LINE__);$ClapsCount=$res->fetch_all(MYSQLI_NUM)[0];$ClapsCount=$ClapsCount[0]+$ClapsCount[1];}$res=$conn->query("SELECT
            GROUP_CONCAT(tg.Name) as tags
            FROM UserTag ut
            LEFT JOIN
            Tags tg
            ON tg.Id = ut.Tag
            WHERE ut.User = $UserId
        ")or fail($conn->error,__LINE__);$UserTags=$res->fetch_all(MYSQLI_NUM)[0][0];$UserTags=explode(",",$UserTags);$res=$conn->query("SELECT
            qn.Id as id,
            qn.Title as title,
            qn.URLTitle as url,
            qn.Author as author
            FROM UserBookmarks ub
            LEFT JOIN
            Question qn
            ON (ub.Question=qn.Id) AND (ub.User = $UserId)
        ;")or fail($conn->error,__LINE__);$userBookMarks=json_encode($res->fetch_all(MYSQLI_ASSOC));$res=$conn->query("SELECT
        qn.Id as id,
        qn.Title as title,
        qn.URLTitle as url,
        qn.Author as author
        FROM Question qn
        WHERE qn.Author=$UserId
    ;");$userQuestions=json_encode($res->fetch_all(MYSQLI_ASSOC));$conn->close(); ?><!doctypehtml><html lang="en"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Askme |<?php echo $UserName; ?></title><link href="/global/global.css"rel="stylesheet"type="text/css"><link href="/global/fonts/all.css"rel="stylesheet"type="text/css"><link href="/profile/profile.css"rel="stylesheet"type="text/css"><script src="/global/global.js"type="text/javascript"></script></head><body onload="Ready()"><?php require('../global/navbar.php'); ?><div id="Main"><div class="profileContainer"><?php if($thisUserId==$UserId){echo "<a class='editProfile' href='/profile_edit/profile_edit.php' style='color: white'>edit</a>";} ?><div class="profileImage"><img height="200"loading="lazy"src="<?php echo $UserPicture; ?>"widht="200"></div><div class="profileInfo"><div class="profileIdentity"><div class="profileName"><?php echo $UserName; ?></div><div class="followBtn"><i class="fa fa-heart follow_icon"></i> <span></span></div><br><span class="profileIntro"><?php echo $UserIntro; ?></span></div><div class="impressionContainer"><a class="hv_border impr questionCount"href="#askedQuestion"><b class="count"><?php echo $QuestionCount; ?></b><span>Questions</span><div class="hoverlay">See Question by<?php echo $UserName; ?></div></a><a class="hv_border impr followingCount"href="#"><b class="count"><?php echo $FollowingCount; ?></b><span>Following</span><div class="hoverlay">Currently<?php echo $UserName; ?>is following<?php echo $FollowingCount; ?>Users</div></a><a class="hv_border impr clapCount"href="#askedQuestion"><b class="count"><?php echo $ClapsCount; ?></b><span>Claps</span><div class="hoverlay">See All Posts<?php echo $UserName; ?>got clapped</div></a><a class="hv_border impr followersCount"href="#"><b class="count"><?php echo $FollowersCount; ?></b><span>Followers</span><div class="hoverlay">Currently<?php echo $FollowersCount; ?>Users are following<?php echo $UserName; ?></div></a><a class="hv_border impr answerCount"href="/answerby/<?php echo $uname; ?>"><b class="count"><?php echo $AnswerCount; ?></b><span>Answers</span><div class="hoverlay">See Answers by<?php echo $UserName; ?></div></a></div></div></div><div class="moreInfo"><div class="infoBlock"id="intrestedIn"><span class="label">Passionate About:</span><div class="innerBlock"><?php foreach($UserTags as&$tag){$tag=trim($tag);if(strlen($tag)==0)continue;echo"<a href='/taggedfor/$tag' class='tag'>$tag</a>";} ?></div></div><div class="infoBlock"id="contactMe"><div class="label">Contact:</div><div class="innerBlock"><div><i class="fa-envelope"></i> <a class="hv_border"href="mailto:<?php echo $UserEmail; ?>"id="Email"><?php echo $UserEmail; ?></a></div><div><i class="fa-phone"></i> <a class="hv_border"href="#"id="Phone"><?php echo $UserPhone; ?></a></div><div><i class="fa-map-marker-alt"></i> <a class="hv_border"href="#"id="Location"><?php echo $UserLocation; ?></a></div></div></div><div class="infoBlock"id="pinnedQuestion"><a name="pinnedQuestion"></a><div class="label"><i class="fa-star"></i> Pinned Question</div><div class="Question"><a class="title"href="#"title="visit this Question"></a> <i class="fa-trash pin_trash"onclick='removeBookMark(this,"id")'title="Remove ths Question from Your Bokmark List.."></i></div></div><div class="infoBlock"id="askedQuestion"><a name="askedQuestion"></a><div class="label"><i class="fa-comment"></i> Asked Question</div></div></div></div><div class="notifyCenter"><div class="notify"style="display:none"></div></div></body><script>var thisUser, profileUser;let userBookMarks=<?php echo $userBookMarks; ?>,userQuestions=<?php echo $userQuestions; ?>,bookmarkCount = 0, questionSample, npq, npqt, npqi;function Ready(){thisUser =<?php echo json_encode($thisUserId); ?>;profileUser =<?php echo json_encode($UserId); ?>;let aw = document.getElementsByClassName('followBtn')[0];if (thisUser==profileUser) {aw.remove();}else if(<?php echo json_encode($isFollowing); ?>==true){follow(aw, false, profileUser);aw.classList.add('active');} else {aw.classList.add('inactive');aw.onclick = function() {follow(aw, true, profileUser);};}questionSample=document.getElementsByClassName("Question")[0];let p=document.getElementById("pinnedQuestion"),count=0;if(userBookMarks.forEach(e=>{null!=e.title&&(fillQuestion(e,p),count++)}),0==count){let e=document.createElement("p");e.textContent="No any pinned Question...",p.appendChild(e)}if(count=0,p=document.getElementById("askedQuestion"),userQuestions.forEach(e=>{fillQuestion(e,p),count++}),0==count){let e=document.createElement("p");e.textContent="No any Question yet...",p.appendChild(e)}notification=document.getElementsByClassName("notify")[0];}function fillQuestion(t,e,o){npq=questionSample.cloneNode(!0),npqt=npq.getElementsByClassName("title")[0],npqt.setAttribute("href","/thread/"+t.url),npqt.textContent=t.title,npqi=npq.getElementsByClassName("pin_trash")[0],t.author==thisUser&&"pinnedQuestion"==e.id?npqi.setAttribute("onclick","removeBookMark(this, "+t.id+")"):t.author==thisUser&&"askedQuestion"==e.id?npqi.setAttribute("onclick","removeQuestion(this, "+t.id+")"):npqi.remove(),e.appendChild(npq)}function unfollow(t,e){quickAction("unfollow",e,function(){notify("You took your follow back :( ");let o=document.getElementsByClassName("followersCount")[0].getElementsByClassName("count")[0];o.textContent=parseInt(o.textContent)-1,t.classList.add("inactive"),t.classList.remove("active"),t.onclick=function(){follow(t,!0,e)}})}function followLastStep(t,e=-1){t.removeAttribute("onclick"),t.classList.add("active"),t.classList.remove("inactive"),t.onclick=function(){unfollow(t,e)}}function follow(t,e=!1,o=-1){!0===e?quickAction("follow",o,function(){notify("You are now following<?php echo $UserName; ?>"),followLastStep(t,o);let e=document.getElementsByClassName("followersCount")[0].getElementsByClassName("count")[0];e.textContent=parseInt(e.textContent)+1}):followLastStep(t,o)}function removeBookMark(t,e){quickAction("removeBookMark",e,function(){for(;!t.classList.contains("Question");)t=t.parentElement;t.remove(),notify("Question had been removed from your list...")})}function removeQuestion(t,e){if(!confirm("Do you really want to delete this Question..."))return 1;quickAction("removeQuestion",e,function(){for(;!t.classList.contains("Question");)t=t.parentElement;t.remove(),notify("That Question had been deleted")})}</script></html>